import React, { useEffect, useState } from "react";
import { Badge, Card, Container, Table } from "react-bootstrap";
import { useSelector } from "react-redux";
import axios from "../../AxiosConfig/axios";
import Loading from "../../components/Loading/Loading";
import "./OrdersPage.css";

function OrdersPage() {
    const user = useSelector((state) => state.user);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true);
        axios
            .get(`/users/${user._id}/orders`)
            .then(({ data }) => {
                setLoading(false);
                setOrders(data);
            })
            .catch((e) => {
                setLoading(false);
                console.log(e);
            });
    }, []);

    if (loading) {
        return <Loading />;
    }

    if (orders.length === 0) {
        return <h1 className="text-center pt-3 bg-info" >No orders yet</h1>;
    }

    return (
        <Container>
            <h1 className="text-center">Your orders</h1>
            <div responsive striped bordered hover>
                {/* <thead>
                    <tr>
                        <th>#</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Total</th>
                    </tr>
                </thead> */}
                <div className="">
                    {orders.map((order) => (
                        <Card>
                        
                            <Card.Text>{order._id}</Card.Text>
                            
                                <Badge bg={`${order.status == "processing" ? "success" : "warning"}`} text="white">
                                    {order.status}
                                </Badge>
                           
                            <Card.Body>{order.date}</Card.Body>

                            <Card.Text>${order.total}</Card.Text>
                       
                        </Card>
                    ))}
                </div>
            </div>
        </Container>
    );
}

export default OrdersPage;
